DefinitionString.Fn::Sub:
{
  "Comment": "Extreme compression pipeline",
  "StartAt": "InspectObject",
  "States": {
    "InspectObject": {
      "Type": "Task",
      "Resource": "${InspectObjectLambdaArn}",
      "Parameters": {
        "sourceBucket.$": "$.sourceBucket",
        "sourceKey.$": "$.sourceKey"
      },
      "ResultPath": "$.inspect",
      "Retry": [
        {
          "ErrorEquals": [
            "Lambda.ServiceException",
            "Lambda.AWSLambdaException",
            "Lambda.SdkClientException",
            "States.TaskFailed"
          ],
          "IntervalSeconds": 2,
          "MaxAttempts": 3,
          "BackoffRate": 2.0
        }
      ],
      "Catch": [
        {
          "ErrorEquals": ["States.ALL"],
          "ResultPath": "$.error",
          "Next": "Fail"
        }
      ],
      "Next": "PrepareArtifact"
    },
    "PrepareArtifact": {
      "Type": "Task",
      "Resource": "${PrepareArtifactLambdaArn}",
      "Parameters": {
        "sourceBucket.$": "$.sourceBucket",
        "sourceKey.$": "$.sourceKey",
        "artifactsBucket": "${ArtifactsBucketName}"
      },
      "ResultPath": "$.prepared",
      "Retry": [
        {
          "ErrorEquals": [
            "Lambda.ServiceException",
            "Lambda.AWSLambdaException",
            "Lambda.SdkClientException",
            "States.TaskFailed"
          ],
          "IntervalSeconds": 2,
          "MaxAttempts": 3,
          "BackoffRate": 2.0
        }
      ],
      "Catch": [
        {
          "ErrorEquals": ["States.ALL"],
          "ResultPath": "$.error",
          "Next": "Fail"
        }
      ],
      "Next": "ExtremeCompression"
    },
    "ExtremeCompression": {
      "Type": "Task",
      "Resource": "${ExtremeCompressionLambdaArn}",
      "Parameters": {
        "artifactsBucket.$": "$.prepared.artifactsBucket",
        "artifactKey.$": "$.prepared.artifactKey",
        "compression": {
          "algorithm": "gzip",
          "level": 9
        }
      },
      "ResultPath": "$.compressed",
      "Retry": [
        {
          "ErrorEquals": [
            "Lambda.ServiceException",
            "Lambda.AWSLambdaException",
            "Lambda.SdkClientException",
            "States.TaskFailed"
          ],
          "IntervalSeconds": 2,
          "MaxAttempts": 2,
          "BackoffRate": 2.0
        }
      ],
      "Catch": [
        {
          "ErrorEquals": ["States.ALL"],
          "ResultPath": "$.error",
          "Next": "Fail"
        }
      ],
      "Next": "StoreFinalObject"
    },
    "StoreFinalObject": {
      "Type": "Task",
      "Resource": "${StoreFinalObjectLambdaArn}",
      "Parameters": {
        "artifactsBucket.$": "$.compressed.artifactsBucket",
        "compressedKey.$": "$.compressed.compressedKey",
        "outputBucket": "${OutputBucketName}"
      },
      "ResultPath": "$.stored",
      "Retry": [
        {
          "ErrorEquals": [
            "Lambda.ServiceException",
            "Lambda.AWSLambdaException",
            "Lambda.SdkClientException",
            "States.TaskFailed"
          ],
          "IntervalSeconds": 2,
          "MaxAttempts": 3,
          "BackoffRate": 2.0
        }
      ],
      "Catch": [
        {
          "ErrorEquals": ["States.ALL"],
          "ResultPath": "$.error",
          "Next": "Fail"
        }
      ],
      "Next": "NotifyUser"
    },
    "NotifyUser": {
      "Type": "Task",
      "Resource": "${NotifyUserLambdaArn}",
      "Parameters": {
        "topicArn": "${SnsTopicArn}",
        "message.$": "States.Format('Compression complete for {}', $.sourceKey)",
        "details.$": "$.stored"
      },
      "ResultPath": "$.notified",
      "Retry": [
        {
          "ErrorEquals": [
            "Lambda.ServiceException",
            "Lambda.AWSLambdaException",
            "Lambda.SdkClientException",
            "States.TaskFailed"
          ],
          "IntervalSeconds": 2,
          "MaxAttempts": 2,
          "BackoffRate": 2.0
        }
      ],
      "Catch": [
        {
          "ErrorEquals": ["States.ALL"],
          "ResultPath": "$.error",
          "Next": "Fail"
        }
      ],
      "End": true
    },
    "Fail": {
      "Type": "Fail",
      "Error": "CompressionFailed",
      "Cause": "Pipeline failure"
    }
  }
}
